var mongoose = require("mongoose");
var categorieSchema = mongoose.Schema({
  nomcategorie: {
    type: String,
    required: true,
    unique: true
  },
  imagecategorie: {
    type: String,
    required: false
  }
});
module.exports = mongoose.model('categorie', categorieSchema);
