"use strict";

var mongoose = require("mongoose");
var scategorie = require("./scategorie.js");
var articleSchema = mongoose.Schema({
  reference: {
    type: String,
    required: true,
    unique: true
  },
  designation: {
    type: String,
    required: true,
    unique: true
  },
  prix: {
    type: Number,
    required: false
  },
  marque: {
    type: String,
    required: true
  },
  qtestock: {
    type: Number,
    required: false
  },
  imageart: {
    type: String,
    required: false
  },
  scategorieID: {
    type: mongoose.Schema.Types.ObjectId,
    ref: scategorie
  }
});
module.exports = mongoose.model('article', articleSchema);
//# sourceMappingURL=data:application/json;charset=utf-8;base64,

sourcesContent:
const mongoose  =require("mongoose")
const scategorie =require("./scategorie.js");
const articleSchema=mongoose.Schema({
reference:{ type: String, required: true,unique:true }, 
designation:{ type: String, required: true,unique:true }, 
prix:{ type: Number, required: false },
marque:{ type: String, required: true },
qtestock:{ type: Number, required: false }, 
imageart:{ type: String, required: false }, 
scategorieID: {type:mongoose.Schema.Types.ObjectId,ref:scategorie}
})
module.exports=mongoose.model('article',articleSchema)
